const basicTemplate=()=>'\n  <template id="hyperfov-link-preview-template">\n  <style>\n    .link-popup-wrapper {\n      padding: 10px;\n      background-color: white;\n      box-shadow: 0px 0px 20px rgba(122, 122, 122, 0.63);\n      border-radius: 6px;\n      max-width: 275px;\n    }\n\n    .link-interior-content div:not(:first-child) {\n      margin-top: 5px;\n    }\n\n    .has-image .link-url,\n    .has-title .link-url,\n    .has-description .link-url,\n    .has-title .link-url {\n      margin-top: 10px;\n    }\n\n    .link-url {\n      text-overflow: ellipsis;\n      overflow: hidden;\n      white-space: nowrap;\n    }\n\n    .link-image {\n      flex-shrink: 0;\n    }\n\n    .link-title {\n      font-weight: bold;\n    }\n\n    ::slotted(img) {\n      max-width: 100%;\n      border-radius: 3px;\n      width: 100%;\n      height: auto;\n      object-fit: cover;\n      object-position: center center;\n    }\n  </style>\n  <div class="link-popup-wrapper">\n    <div class="link-interior-content">\n      <div class="link-image"><slot name="image"></slot></div>\n      <div class="link-title"><slot name="title"></slot></div>\n      <div class="link-description">\n        <slot name="description"></slot>\n      </div>\n    </div>\n    <div class="link-url"><slot name="url"></slot></div>\n  </div>\n</template>';export default basicTemplate;